CREATE TABLE "pelicula" (
	"id_pelicula" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"titulo" VARCHAR(50) NOT NULL,
	"descripcion" VARCHAR(255),
	"duracion" INTEGER NOT NULL,
	PRIMARY KEY("id_pelicula")
);




CREATE TABLE "actor" (
	"id_actor" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"nombre" VARCHAR(50) NOT NULL,
	"apellido" VARCHAR(50) NOT NULL,
	"edad" INTEGER NOT NULL,
	"id_nacion" INTEGER NOT NULL,
	PRIMARY KEY("id_actor")
);




CREATE TABLE "nacion" (
	"id_nacion" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"Nombre" VARCHAR(50) NOT NULL,
	PRIMARY KEY("id_nacion")
);




CREATE TABLE "elenco" (
	"id_elenco" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"id_actor" INTEGER NOT NULL,
	"id_pelicula" INTEGER NOT NULL,
	PRIMARY KEY("id_elenco")
);



ALTER TABLE "actor"
ADD FOREIGN KEY("id_nacion") REFERENCES "nacion"("id_nacion")
ON UPDATE NO ACTION ON DELETE NO ACTION;
ALTER TABLE "elenco"
ADD FOREIGN KEY("id_pelicula") REFERENCES "pelicula"("id_pelicula")
ON UPDATE NO ACTION ON DELETE NO ACTION;
ALTER TABLE "elenco"
ADD FOREIGN KEY("id_actor") REFERENCES "actor"("id_actor")
ON UPDATE NO ACTION ON DELETE NO ACTION;











